LEVEL.FINISH:
prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}    
levelcomplete=true;
if( prand != 0 || myplot.data.length < 3 || myplot.data[2][1]!=5 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){ 
     if( myplot.data[i][0]!=(i-2) || myplot.data[i][1]!=(i+3) ){ levelcomplete=false; }
}

prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}   
levelcomplete=true;
if( prand != 1 || myplot.data.length < 3 || myplot.data[2][1]!=5 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( myplot.data[i][0]!=(i-2) || myplot.data[i][1]-Math.floor(myplot.data[i][1])!=0  ){ levelcomplete=false; }
     if( i>2 ){
         if( Math.abs( myplot.data[i][1]-myplot.data[i-1][1] ) != 1 ){ levelcomplete=false; }
     }
}

prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true;
if( prand != 1 || myplot.data.length < 3 || myplot.data[2][1]!=5 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( myplot.data[i][0]!=(i-2) || myplot.data[i][1]-Math.floor(myplot.data[i][1])!=0 || myplot.data[i][1] < 0 || myplot.data[i][1] > 20  ){ levelcomplete=false; }
     if( i>2 ){
         if( Math.abs( myplot.data[i][1]-myplot.data[i-1][1] ) != 1 ){ levelcomplete=false; }
     }
}
if( myplot.data[myplot.data.length-1][1] != 0 && myplot.data[myplot.data.length-1][1] != 20 ){ levelcomplete=false; }
