LEVEL.FINISH:
prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true;
if( prand != 1 && myplot.data.length < 3 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( myplot.data[i][1]!=0 && myplot.data[i][1]!=1 && myplot.data[i][1]!=2 && myplot.data[i][1]!=3 && myplot.data[i][1]!=4 && myplot.data[i][1]!=5 &&myplot.data[i][1]!=6 ){ levelcomplete=false; }
     if( myplot.data[i][0]!=(i-1) ){ levelcomplete=false; }
}

prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true;
if( prand != 1 && myplot.data.length != 9 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( myplot.data[i][0]!=(i-2) || myplot.data[i][1] > 20 || myplot.data[i][1] < 0 || myplot.data[i][1]-Math.floor(myplot.data[i][1])!=0 ){ levelcomplete=false; }
}

prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true; var sum=0;
if( prand != 1 && myplot.data.length != 9 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     sum += myplot.data[i][1];
     if( myplot.data[i][0]!=(i-2) || myplot.data[i][1] > 1.0 || myplot.data[i][1] < 0 ){ levelcomplete=false; }
}
if( sum > 1.05 || sum < 0.95 ){ levelcomplete=false; }

prand = 0; 
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="standard normal random variable" ){ prand += 1; }
}
levelcomplete=true; var sum=0;
if( prand != 1 && myplot.data.length != 22 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     sum += myplot.data[i][1]
     if( myplot.data[i][0]!=(-3.8+(i-2)*0.4) || myplot.data[i][1] > 1.0 || myplot.data[i][1] < 0 ){ levelcomplete=false; }
}
if( sum > 1.05 || sum < 0.95 ){ levelcomplete=false; }
