prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true;
if( prand != 1 || myplot.data.length < 3 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( myplot.data[i][0]!=(i-1) ){ levelcomplete=false; }
}

prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true;
if( prand != 1 || myplot.data.length < 3 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( myplot.data[i][1]-Math.floor(myplot.data[i][1])!=0 ){ levelcomplete=false; }
     if( i>2 ){
         if( myplot.data[i][1]-myplot.data[i-1][1] != 1 ){ levelcomplete=false; }
         if( myplot.data[i][0] < myplot.data[i-1][0] ){ levelcomplete=false; }
     }
}

prand = 0; pnorm = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
     if( ww[i]=="standard normal random variable" ){ pnorm += 1; }
}
levelcomplete=true;  
if( prand != 1 || pnorm != 1 || myplot.data.length < 3 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( i>2 ){
         if( myplot.data[i][1]-myplot.data[i-1][1] < 0 ){ levelcomplete=false; }
         if( myplot.data[i][0] < myplot.data[i-1][0] ){ levelcomplete=false; }
     }
}

prand = 0; 
for(var i=0; i < ww.length; i++ ){
    if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
if( prand != 2 || myplot.data.length != 3 ){ levelcomplete=false; }
if( myplot.error.length!=myplot.data.length-1 || myplot.data[i][2] < 0 || myplot.data[i][2] > 1 ){ levelcomplete=false; }

prand = 0; 
for(var i=0; i < ww.length; i++ ){ 
    if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
if( prand != 3 || myplot.data.length != 3 ){ levelcomplete=false; }
if( myplot.error.length!=myplot.data.length-1 || myplot.data[i][2] < 0 || myplot.data[i][2] > 1 ){ levelcomplete=false; }

levelcomplete=true; var sum=0;
if( myplot.data.length != 5 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     sum += myplot.data[i][1]
     if( myplot.data[i][0] != (i-1) || myplot.data[i][1] > 1.0 || myplot.data[i][1] < 0 ){ levelcomplete=false; }
}
if( sum > 1.05 || sum < 0.95 ){ levelcomplete=false; }

