LEVEL.FINISH:
prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true;
if( prand != 1 || myplot.data.length != 3 || myplot.data[2][0]!=1 ){ levelcomplete=false; }

prand = 0;
for(var i=0; i < ww.length; i++ ){
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true;
if( prand != 1 || myplot.data.length < 3 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( myplot.data[i][1]!=0 && myplot.data[i][1]!=1 ){ levelcomplete=false; }
     if( myplot.data[i][0]!=(i-1) ){ levelcomplete=false; }
}

prand = 0;
for(var i=0; i < ww.length; i++ ){ 
     if( ww[i]=="uniform random variable between 0 and 1" ){ prand += 1; }
}
levelcomplete=true;
if( prand != 1 || myplot.data.length < 12 ){ levelcomplete=false; }
for(var i=2; i < myplot.data.length; i++ ){
     if( myplot.data[i][1]!=0 && myplot.data[i][1]!=1 ){ levelcomplete=false; }
     if( myplot.data[i][0]!=(i-1) ){ levelcomplete=false; }
}
